import React, { useState } from 'react';
import s from './SpicinessFilter.module.scss';

const SpicinessFilter = ({ filters, setFilters }) => {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleSpicinessChange = (value) => {
    setFilters((prevFilters) => {
      const isSelected = filters.spiciness.includes(value);
      const newSpiciness = isSelected
        ? prevFilters.spiciness.filter((level) => level !== value)
        : [...prevFilters.spiciness, value];

      return { ...prevFilters, spiciness: newSpiciness };
    });
  };

  return (
    <div className={s.filterItem}>
      <h3
        onClick={() => setShowDropdown(!showDropdown)}
        className={s.dropdownHeader}
      >
        Degré de piquance
      </h3>
      {showDropdown && (
        <div className={s.spicinessOptions}>
          <label>
            <input
              type="checkbox"
              value="mild"
              checked={filters.spiciness.includes("mild")}
              onChange={() => handleSpicinessChange("mild")}
            />
            Légèrement épicé
          </label>
          <label>
            <input
              type="checkbox"
              value="medium"
              checked={filters.spiciness.includes("medium")}
              onChange={() => handleSpicinessChange("medium")}
            />
            Pique fort
          </label>
          <label>
            <input
              type="checkbox"
              value="hot"
              checked={filters.spiciness.includes("hot")}
              onChange={() => handleSpicinessChange("hot")}
            />
            Littéralement de la lave
          </label>
        </div>
      )}
    </div>
  );
};

export default SpicinessFilter;
