import React, { useEffect, useRef } from 'react';
import SpicinessFilter from './SpicinessFilter';
import VegetarianFilter from './VegetarianFilter';
import VeganFilter from './VeganFilter';
import RamenTypeFilter from './RamenTypeFilter';
import s from './FilterSection.module.scss';

const FilterSection = ({ filters, setFilters }) => {
  const filterContainerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const filterContainer = filterContainerRef.current;
      const footer = document.getElementById('footer');

      if (filterContainer && footer) {
        const top = filterContainer.offsetTop;
        const footerTop = footer.offsetTop;

        if (window.scrollY >= top && window.scrollY + filterContainer.offsetHeight < footerTop) {
          filterContainer.classList.add(s.sticky);
        } else {
          filterContainer.classList.remove(s.sticky);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div ref={filterContainerRef} className={s.filter_container}>
      <SpicinessFilter filters={filters} setFilters={setFilters} />
      <VegetarianFilter filters={filters} setFilters={setFilters} />
      <VeganFilter filters={filters} setFilters={setFilters} />
      <RamenTypeFilter filters={filters} setFilters={setFilters} />
    </div>
  );
};

export default FilterSection;
