import React from 'react';
import s from './RamenTypeFilter.module.scss';

const RamenTypeFilter = ({ filters, setFilters }) => {
  const handleChange = (event) => {
    const { name, checked } = event.target;
    setFilters({
      ...filters,
      types: checked
        ? [...filters.types, name]
        : filters.types.filter(type => type !== name),
    });
  };

  return (
    <div className={s.filterContainer}>
      <h3>Type de Ramen</h3>
      <label>
        <input
          type="checkbox"
          name="shio"
          checked={filters.types.includes("shio")}
          onChange={handleChange}
        />
        Shio Ramen
      </label>
      <label>
        <input
          type="checkbox"
          name="tonkotsu"
          checked={filters.types.includes("tonkotsu")}
          onChange={handleChange}
        />
        Tonkotsu Ramen
      </label>
      <label>
        <input
          type="checkbox"
          name="shoyu"
          checked={filters.types.includes("shoyu")}
          onChange={handleChange}
        />
        Shoyu Ramen
      </label>
    </div>
  );
};


export default RamenTypeFilter;
