import React from 'react';
import s from './SpicinessFilter.module.scss';

const SpicinessFilter = ({ filters, setFilters }) => {
  const handleChange = (event) => {
    const { name, checked } = event.target;
    setFilters({
      ...filters,
      spiciness: checked
        ? [...filters.spiciness, parseInt(name, 10)]
        : filters.spiciness.filter(spice => spice !== parseInt(name, 10)),
    });
  };

  return (
    <div className={s.filterContainer}>
      <h3>Degr√© de Piquance</h3>
      <label>
        <input
          type="checkbox"
          name="1"
          checked={filters.spiciness.includes(1)}
          onChange={handleChange}
        />
        1 Piment sur 3
      </label>
      <label>
        <input
          type="checkbox"
          name="2"
          checked={filters.spiciness.includes(2)}
          onChange={handleChange}
        />
        2 Piments sur 3
      </label>
      <label>
        <input
          type="checkbox"
          name="3"
          checked={filters.spiciness.includes(3)}
          onChange={handleChange}
        />
        3 Piments sur 3
      </label>
    </div>
  );
};


export default SpicinessFilter;
