import React from 'react';
import s from './RamenTypeFilter.module.scss';

const RamenTypeFilter = ({ filters, setFilters }) => {
  const handleChange = (event) => {
    const { name, checked } = event.target;
    setFilters({
      ...filters,
      types: {
        ...filters.types,
        [name]: checked,
      },
    });
  };

  return (
    <div className={s.filterContainer}>
      <h3>Type de Ramen</h3>
      <label>
        <input
          type="checkbox"
          name="shio"
          checked={filters.types.shio || false}
          onChange={handleChange}
        />
        Shio Ramen
      </label>
      <label>
        <input
          type="checkbox"
          name="tonkotsu"
          checked={filters.types.tonkotsu || false}
          onChange={handleChange}
        />
        Tonkotsu Ramen
      </label>
      <label>
        <input
          type="checkbox"
          name="shoyu"
          checked={filters.types.shoyu || false}
          onChange={handleChange}
        />
        Shoyu Ramen
      </label>
    </div>
  );
};

export default RamenTypeFilter;
