import React from "react";
import s from './RamenCard.module.scss';

const RamenCard = ({ ramen, onAddToCart }) => {
  const getSpicinessLabel = (spiciness) => {
    switch (spiciness) {
      case "mild":
        return "Légèrement épicé";
      case "medium":
        return "Pique fort";
      case "hot":
        return "Littéralement de la lave";
      default:
        return "";
    }
  };

  return (
    <div className={s.ramenCard}>
      <h2>{ramen.title}</h2>
      <p>{ramen.description}</p>
      <p className={s.spicinessLabel}>{getSpicinessLabel(ramen.spiciness)}</p> {/* Ajout du label de piquance */}
      <button onClick={() => onAddToCart(ramen)}>Ajouter au panier</button>
    </div>
  );
};

export default RamenCard;
