import React from "react";

const SpicinessFilter = ({ filters, setFilters }) => {
  const handleSpicinessChange = (event) => {
    const value = parseInt(event.target.value);
    const newSpiciness = filters.spiciness.includes(value)
      ? filters.spiciness.filter((spice) => spice !== value)
      : [...filters.spiciness, value];
    setFilters({ ...filters, spiciness: newSpiciness });
  };

  return (
    <div className="filter-spiciness">
      <label>Piquance</label>
      <div>
        <input
          type="checkbox"
          value={1}
          checked={filters.spiciness.includes(1)}
          onChange={handleSpicinessChange}
        />
        1 piment sur 3
      </div>
      <div>
        <input
          type="checkbox"
          value={2}
          checked={filters.spiciness.includes(2)}
          onChange={handleSpicinessChange}
        />
        2 piments sur 3
      </div>
      <div>
        <input
          type="checkbox"
          value={3}
          checked={filters.spiciness.includes(3)}
          onChange={handleSpicinessChange}
        />
        3 piments sur 3
      </div>
    </div>
  );
};

export default SpicinessFilter;
