import React from "react";

const RamenTypeFilter = ({ filters, setFilters }) => {
  const handleTypeChange = (event) => {
    const value = event.target.value;
    const newTypes = filters.types.includes(value)
      ? filters.types.filter((type) => type !== value)
      : [...filters.types, value];
    setFilters({ ...filters, types: newTypes });
  };

  return (
    <div className="filter-ramen-type">
      <label>Type de Ramen</label>
      <div>
        <input
          type="checkbox"
          value="Shio"
          checked={filters.types.includes("Shio")}
          onChange={handleTypeChange}
        />
        Shio Ramen
      </div>
      <div>
        <input
          type="checkbox"
          value="Tonkotsu"
          checked={filters.types.includes("Tonkotsu")}
          onChange={handleTypeChange}
        />
        Tonkotsu Ramen
      </div>
      <div>
        <input
          type="checkbox"
          value="Shoyu"
          checked={filters.types.includes("Shoyu")}
          onChange={handleTypeChange}
        />
        Shoyu Ramen
      </div>
    </div>
  );
};

export default RamenTypeFilter;
